@js:
function removeParent(doc,txt,index){
e=doc.select(txt).get(0);
while(e!=null&&e.hasParent()&&index>0){
e=e.parent()
index--
}
if(e!=null){e.remove()}
}
var doc=org.jsoup.Jsoup.parse(result,baseUrl);
//去页眉
doc.select('#header-top').remove();
//去页脚
doc.select('.myui-foot').remove();
//去统计
doc.select('[src*="dlads.cn"]').remove();
//去猜你喜欢
