.menu {
	justify-content: space-between;
	max-width: 900px;
	margin: 0 5px;
}
.menu .button {
	width: 55px;
	height: 15px;
	min-height: 5px;
	margin: 5px 0px;
	cursor: pointer;
}
.button text {
	text-anchor: middle;
	dominant-baseline: middle;
}

.button rect {
	width: 100%;
	height: 100%;
	fill: transparent;
	stroke: #666;
	stroke-width: 2px;
}
.button rect.busy {
	stroke: #fD1850;
	stroke-dasharray: 30 90;
	animation: stroker 1s linear infinite;
}
.tabbox {
	flex: 1;
	display: flex;
	flex-flow: column;
}
.tabtitle {
	display: flex;
	z-index: 1;
	justify-content: center;
}
.tabtitle>div {
	cursor: pointer;
	padding: 1px 10px 0 10px;
	border-bottom: 3px solid transparent;
	font-weight: bold;
}
.tabtitle>.this {
	color: #4f9da6;
	border-bottom-color: #4EBBE4;
}
.tabbody {
	flex: 1;
	display: flex;
	margin-top: -1px;
	border: 1px solid #A9A9A9;
	height: 0;
}
.tabbody>* {
	flex: 1;
	flex-flow: column;
	display: none;
}
.tabbody>.this {
	display: flex;
}
.tabbody>*>.titlebar{
	display: flex;
}
.tabbody>*>.titlebar>*{
	flex: 1;
	margin: 1px 1px 1px 1px;
}
.tabbody>*>.context {
	flex: 1;
	flex-flow: column;
	border: 0;
	padding: 5px;
	overflow-y: auto;
}
.tabbody>*>.inputbox{
	border: 0;
	border-bottom: #A9A9A9 solid 1px;
	height: 15px;
	text-align:center;
}
.link>* {
	display: flex;
	margin: 5px;
	border-bottom: 1px solid;
	text-decoration: none;
}
#RuleList>label>* {
	background: #eee;
	padding-left: 3px;
	margin: 2px 0;
